mapper.insert:
INSERT INTO photoboard(
    memberId
    , photoContent
    , photoDate
    , photoImg1
    , photoImg2
    , photoImg3
    , photoImg4
    , photoImg5
    , areaCode
    , styleCode
    , houseCode
    , placeCode
    , likeCount
    , scrapCount )
VALUE (
    #{memberId}
    , #{photoContent}
    , curdate()
    , #{photoImg1}
    , #{photoImg2}
    , #{photoImg3}
    , #{photoImg4}
    , #{photoImg5}
    , #{areaCode}
    , #{styleCode}
    , #{houseCode}
    , #{placeCode}
    , 0
    , 0
    )

mapper.update:
UPDATE photoboard SET
    photoContent = #{photoContent}
    , photoImg1 = #{photoImg1}
    , photoImg2 = #{photoImg2}
    , photoImg3 = #{photoImg3}
    , photoImg4 = #{photoImg4}
    , photoImg5 = #{photoImg5}
    , areaCode = #{areaCode}
    , styleCode = #{styleCode}
    , houseCode = #{houseCode}
    , placeCode = #{placeCode}
WHERE photoBoardNo = #{photoBoardNo}

mapper.select:
SELECT photoBoardNo, photoImg1
FROM photoboard
WHERE memberId = #{memberId}
    ORDER BY photoBoardNO desc
    LIMIT 4;

mapper.insert:
INSERT INTO thehouse.like(
    likeNo
    , memberId
    , photoBoardNo
    )
VALUES (
    #{likeNo}
    , #{memberId}
    , #{photoBoardNo}
    )

mapper.delete:
DELETE FROM thehouse.like
WHERE memberId = #{memberId}
    AND photoBoardNo = #{photoBoardNo}

mapper.update:
UPDATE photoboard SET
    likeCount = likeCount + 1
WHERE photoBoardNo = #{photoBoardNo}

UPDATE photoboard SET
    likeCount = likeCount -1
WHERE photoBoardNo = #{photoBoardNo}

mapper.select:
SELECT count(*)
FROM thehouse.like
WHERE memberId = #{memberId}
    AND photoBoardNo = #{photoBoardNo}

mapper.insert:
INSERT INTO scrap(
    memberId
    , photoBoardNo)
VALUES (
    #{memberId}
    , #{photoBoardNo}
    )

mapper.delete:
DELETE FROM scrap
WHERE memberId = #{memberId}
    AND photoBoardNo = #{photoBoardNo}

mapper.update:
UPDATE photoboard SET
    scrapCount = scrapCount + 1
WHERE photoBoardNo = #{photoBoardNo}

UPDATE photoboard SET
    scrapCount = scrapCount -1
WHERE photoBoardNo = #{photoBoardNo}

mapper.select:
SELECT count(*)
FROM scrap
WHERE memberId = #{memberId}
    AND photoBoardNo = #{photoBoardNo}

mapper.insert:
INSERT INTO comment(
    memberId
    , commentupDate
    , commentContent
    , photoBoardNo
    )
VALUES (
    #{memberId}
    , curdate()
    , #{commentContent}
    , #{photoBoardNo}
    )

mapper.select:
